    this.notify('open')
  }

  send() {
    // unused
  }

  close() {
    this.socketStatus = 3
    this.notify('close')
  }

  // mocking helpers
  mockError() {
    this.notify('error', { message: 'An error' })
  }

  mockMessage() {
    this.notify(new Message(['test/path'], 55.1, 224))
  }
}

/** @test {OSC} */
describe('OSC', () => {
  let osc
  let plugin

  before(() => {
    plugin = new TestPlugin()
    osc = new OSC({
